// Obtener datos
const tipo = $json.tipo;
const params = $json.parametros;
const instancia = $json.instancia;

// Construir la consulta SQL base
let sql = `SELECT * FROM compras WHERE instancia = '${instancia}'`;
let conditions = [];

// Agregar filtros segÃºn parÃ¡metros disponibles

// Filtro de fechas (cambiado a fecha_registro)
if (params.fecha_inicio && params.fecha_fin) {
  conditions.push(`fecha_registro::date BETWEEN '${params.fecha_inicio}' AND '${params.fecha_fin}'`);
} else if (params.fecha_inicio) {
  conditions.push(`fecha_registro::date = '${params.fecha_inicio}'`);
}

// Filtro de proveedor
if (params.proveedor) {
  conditions.push(`proveedor ILIKE '%${params.proveedor}%'`);
}

// Filtro de producto
if (params.producto) {
  conditions.push(`productos::text ILIKE '%${params.producto}%'`);
}

// Filtro de nÃºmero de factura
if (params.numero_factura) {
  conditions.push(`numero_factura = '${params.numero_factura}'`);
}

// Unir todas las condiciones
if (conditions.length > 0) {
  sql += ' AND ' + conditions.join(' AND ');
}

// Ordenar por fecha_registro descendente
sql += ' ORDER BY fecha_registro DESC, created_at DESC';

// Si es tipo "total", modificar para calcular suma
let sqlTotal = null;
if (tipo === 'total') {
  sqlTotal = sql.replace('SELECT *', 'SELECT SUM(total) as total_gastado, COUNT(*) as num_facturas');
}

return {
  json: {
    sql: sql,
    sql_total: sqlTotal,
    tipo: tipo,
    instancia: instancia,
    telefono: $json.telefono,
    mensaje_original: $json.mensaje_original,
    parametros: params
  }
};
