parameters.jsCode:
// Obtener los apodos del input actual
const apodosInput = $input.first().json.apodos;

// Obtener el producto insertado del nodo anterior
const productoInsertado = $('Insertar Producto').first().json;

// Si no hay apodos, retornar vac√≠o
if (!apodosInput || apodosInput.trim() === '') {
  return [];
}

// Procesar apodos (separar por comas y limpiar)
const apodos = apodosInput
  .split(',')
  .map(a => a.trim())
  .filter(a => a !== '');

// Crear un objeto por cada apodo para insertar en la tabla
return apodos.map(apodo => ({
  tenant_id: productoInsertado.tenant_id || $('Insertar Producto').first().json.tenant_id,
  producto_id: productoInsertado.producto_id,
  apodo: apodo.toLowerCase()
}));

// Obtener el producto insertado
const productoInsertado = $('Insertar Producto').first().json;

// Obtener unidad y stock de datos_mapeados
const unidad = $('datos_mapeados').first().json.unidad;
const factor = $('datos_mapeados').first().json.factor;
const stock = $('datos_mapeados').first().json.stock;

// Obtener los apodos insertados (puede ser un array)
const apodosInsertados = $input.all();

// Obtener datos del usuario
const nombreNegocio = $('Validar token').first().json.nombre_negocio;
const nombreUsuario = $('Validar token').first().json.nombre_usuario;

// Formatear la lista de apodos con sus c√≥digos
let listaApodos = "";
if (apodosInsertados.length > 0) {
  listaApodos = apodosInsertados.map(item => {
    return `  ‚Ä¢ ${item.json.apodo} (${item.json.apodo_id})`;
  }).join('\n');
} else {
  listaApodos = "  _Sin apodos_";
}

// Construir el mensaje
const mensaje = `‚úÖ *¬°Producto creado exitosamente!*

üì¶ *Producto:* ${productoInsertado.producto}
üî¢ *C√≥digo:* ${productoInsertado.codigo}
üìè *Unidad:* ${unidad} (x${factor} unidades)
üí∞ *Precio:* $${parseFloat(productoInsertado.precio_unitario).toLocaleString('es-CO')}
üíµ *Costo:* $${parseFloat(productoInsertado.costo || 0).toLocaleString('es-CO')}
üìä *Stock inicial:* ${stock} unidades

üè∑Ô∏è *Apodos registrados:*
${listaApodos}

üë§ *Creado por:* ${nombreUsuario}
üè™ *Negocio:* ${nombreNegocio}

_El producto ya est√° disponible para ventas._`;

return {
  mensaje: mensaje,
  producto_id: productoInsertado.producto_id,
  codigo: productoInsertado.codigo,
  producto: productoInsertado.producto
};
