// Obtener la respuesta de Gemini
const geminiResponse = $input.first().json;

// Extraer el texto de la respuesta
const textResponse = geminiResponse.content?.parts?.[0]?.text || '';

// Limpiar los bloques de cÃ³digo markdown (```json ... ```)
const jsonText = textResponse
  .replace(/```json\n/g, '')
  .replace(/```/g, '')
  .trim();

// Parsear el JSON
let parsedData;
try {
  parsedData = JSON.parse(jsonText);

  // Formatear los IDs: agregar P si no lo tienen y hacer padding
  if (parsedData.venta_ids && Array.isArray(parsedData.venta_ids)) {
    parsedData.venta_ids = parsedData.venta_ids.map(id => {
      const ventaId = id.toString().trim();

      // Si es solo nÃºmero, agregar P y padding
      if (/^\d+$/.test(ventaId)) {
        return 'P' + ventaId.padStart(3, '0');
      }
      // Si ya tiene P, asegurar el formato correcto
      else if (/^P\d+$/i.test(ventaId)) {
        const numero = ventaId.substring(1);
        return 'P' + numero.padStart(3, '0');
      }
      // Si tiene otro formato, devolver tal cual
      return ventaId;
    });

    // Validar que haya al menos un ID
    parsedData.valido = parsedData.venta_ids.length > 0;
  } else {
    parsedData.valido = false;
    parsedData.venta_ids = [];
  }

} catch (error) {
  parsedData = {
    venta_ids: [],
    valido: false,
    error: 'No se pudo parsear la respuesta'
  };
}

return {
  json: parsedData
};

