// Obtener detalles de cada venta
const detalles = allData.map(item => {
  return `âœ… ${item.json.codigo_venta}: ${item.json.producto} (${item.json.cantidad} unidades) - $${item.json.valor_total}`;
}).join('\n');

return [{
  json: {
    success: true,
    total_revertidas: ventasRevertidas,
