// Obtener el texto desde la ruta indicada
let texto = $input.first().json.estado.mensaje_original || "";

// Diccionario básico de números en palabras a números
const numeros = {
  "cero": "0",
  "uno": "1", "una": "1", "un": "1",
  "dos": "2",
  "tres": "3",
  "cuatro": "4",
  "cinco": "5",
  "seis": "6",
  "siete": "7",
  "ocho": "8",
  "nueve": "9",
  "diez": "10",
  "once": "11",
  "doce": "12",
  "trece": "13",
  "catorce": "14",
  "quince": "15",
  "dieciséis": "16", "dieciseis": "16",
  "diecisiete": "17",
  "dieciocho": "18",
  "diecinueve": "19",
  "veinte": "20",
  "veintiuno": "21", "veintiuna": "21",
  "veintidós": "22", "veintidos": "22",
  "veintitrés": "23", "veintitres": "23",
  "veinticuatro": "24",
  "veinticinco": "25",
  "veintiséis": "26", "veintiseis": "26",
  "veintisiete": "27",
  "veintiocho": "28",
  "veintinueve": "29",
  "treinta": "30",
  "cuarenta": "40",
  "cincuenta": "50",
  "sesenta": "60",
  "setenta": "70",
  "ochenta": "80",
  "noventa": "90",

};

// Función para convertir palabras a números o símbolos
function palabraANumero(palabra) {
  palabra = palabra.toLowerCase().trim();

  // Conversión de "punto" a "."
  if (palabra === "punto") {
    return ".";
  }

  if (numeros.hasOwnProperty(palabra)) {
    return numeros[palabra];
  }

  // Manejo de combinaciones como "treinta y cinco"
  if (palabra.includes(" y ")) {
    let partes = palabra.split(" y ").map(p => p.trim());
    if (numeros[partes[0]] && numeros[partes[1]]) {
      return (parseInt(numeros[partes[0]]) + parseInt(numeros[partes[1]])).toString();
    }
  }

  return palabra; // si no se reconoce, se deja igual
}

// Procesar el texto palabra por palabra
let palabras = texto.split(/(\s+)/); // Mantiene los espacios
let resultado = palabras.map(palabra => {
  // Solo procesar si no es un espacio y contiene letras
  if (palabra.trim() && /[a-záéíóúñ]/i.test(palabra)) {
    return palabraANumero(palabra);
  }
  return palabra;
}).join('');

return { texto_convertido: resultado };

// En lugar de referenciar otros nodos, usar el input directo
return [{
  json: {
    texto_para_administrador: $json.mensaje_final || $json.final_message || "Error"
  }
}];

// Obtener el mensaje final del input
const finalMessage = $input.first().json.final_message;

// Función para procesar el mensaje
function procesarCombo(mensaje) {
  // Expresión regular para encontrar "combo" o "combos" con su cantidad
  const regex = /(\d+)\s*(combo|combos)/gi;

  // Verificar si el mensaje contiene "combo" o "combos"
  if (!regex.test(mensaje)) {
    // Si no contiene combo, devolver el mensaje original
    return mensaje;
  }

  // Resetear el regex para usarlo de nuevo
  regex.lastIndex = 0;

  // Reemplazar cada ocurrencia de combo
  const mensajeModificado = mensaje.replace(regex, (match, cantidad) => {
    return `${cantidad} "Arepa con Queso" ${cantidad} "Chorizos"`;
  });

  return mensajeModificado;
}

// Procesar el mensaje
const mensajeProcesado = procesarCombo(finalMessage);

// Retornar el resultado
return {
  json: {
    final_message: mensajeProcesado,
    original_message: finalMessage
  }
};

// Obtener los datos extraídos
const input = $input.first().json.output;

// Extraer información general
const proveedor = input["Nombre o razon social del proveedor"];
const nit = input["Nit del proveedor"];
const numeroFactura = input["Numero de factura"];
const fechaEmision = input["Fecha de emisión"];
const totalFactura = input["TOTAL"];

// Parsear el string JSON de productos
let productos = [];
try {
  const productosString = input["productos"] || input["Nombre del articulo, cantidad y total."];
  productos = JSON.parse(productosString);
} catch (error) {
  console.error("Error al parsear productos:", error);
  return [{ json: { 
    error: "No se pudieron procesar los productos",
    datos_factura: input
  }}];
}

// Preparar datos para el siguiente paso (búsqueda de matches)
return [{
  json: {
    proveedor: proveedor,
    nit_proveedor: nit,
    numero_factura: numeroFactura,
    fecha_factura: fechaEmision,
    total_factura: totalFactura,
    productos: productos, // Array de productos de la factura
    productos_procesados: [] // Se llenará después del matching
  }
}];

// Obtener productos del inventario desde el input actual
const productosInventario = $input.all().map(item => item.json);

// Obtener datos de la factura desde el nodo Code anterior
const datosFactura = $('Code in JavaScript').first().json;
const productosFactura = datosFactura.productos;

// Crear lista de productos disponibles como texto
const listaProductos = productosInventario.map((p, i) => `${i + 1}. ${p.producto}`).join('\n');

// Crear mensaje para cada producto de la factura
const productosParaProcesar = productosFactura.map((prodFactura, index) => {
  return {
    producto_factura: prodFactura.nombre,
    cantidad: prodFactura.cantidad,
    valor_total: prodFactura.valor_total,
    productos_disponibles: listaProductos,
    lista_productos_array: productosInventario, // Para usar después
    numero_producto: index + 1,
    total_productos: productosFactura.length,
    // Datos de la factura
    proveedor: datosFactura.proveedor,
    nit_proveedor: datosFactura.nit_proveedor,
    numero_factura: datosFactura.numero_factura,
    fecha_factura: datosFactura.fecha_factura,
    total_factura: datosFactura.total_factura
  };
});

return productosParaProcesar.map(p => ({ json: p }));

// Obtener la respuesta de la IA
const respuestaIA = $input.first().json.content.parts[0].text;

// Limpiar el JSON (remover ```json y ```)
const jsonLimpio = respuestaIA.replace(/```json\n?/g, '').replace(/```/g, '').trim();

// Parsear el JSON
let resultadoMatch;
try {
  resultadoMatch = JSON.parse(jsonLimpio);
} catch (error) {
  console.error("Error parseando respuesta IA:", error);
  resultadoMatch = {
    match_automatico: false,
    producto_principal: { numero: null, confianza: 0, razon: "Error al procesar" },
    productos_similares: []
  };
}

// Obtener datos del producto desde el nodo anterior
const datosProducto = $('Preparar_matching').first().json;

// Combinar todo
return [{
  json: {
    // Datos del producto de la factura
    producto_factura: datosProducto.producto_factura,
    cantidad: datosProducto.cantidad,
    valor_total: datosProducto.valor_total,

    // Resultado del matching
    match_automatico: resultadoMatch.match_automatico || false,
    producto_principal: resultadoMatch.producto_principal || {},
    productos_similares: resultadoMatch.productos_similares || [],

    // Datos de la factura
    proveedor: datosProducto.proveedor,
    nit_proveedor: datosProducto.nit_proveedor,
    numero_factura: datosProducto.numero_factura,
    fecha_factura: datosProducto.fecha_factura,
    total_factura: datosProducto.total_factura,

    // Lista completa de productos (por si acaso)
    productos_disponibles: datosProducto.productos_disponibles,
    lista_productos_array: datosProducto.lista_productos_array
  }
}];

// Obtener datos del producto
const datos = $input.first().json;

// Formatear valor
const valorFormateado = datos.valor_total.toLocaleString('es-CO');

// Crear lista de productos similares
let listaProductosSimilares = "";
let hayProductosSimilares = false;

if (datos.productos_similares && datos.productos_similares.length > 0) {
  hayProductosSimilares = true;
  listaProductosSimilares = datos.productos_similares
    .slice(0, 5) // Máximo 5 productos
    .map((p, index) => {
      const estrellas = p.confianza >= 60 ? " ⭐" : "";
      return `${p.numero}. ${p.nombre} (${p.confianza}% similar)${estrellas}`;
    })
    .join('\n');
} else {
  listaProductosSimilares = "❌ No hay productos similares en el inventario";
}

// Crear mensaje
const mensaje = `📦 *Producto en factura*

🏭 Proveedor: ${datos.proveedor}
📄 Factura: ${datos.numero_factura}
📅 Fecha: ${datos.fecha_factura}

❓ Producto: *${datos.producto_factura}*
📊 Cantidad: ${datos.cantidad}
💰 Valor: $${valorFormateado}

━━━━━━━━━━━━━━━━━━━━

${hayProductosSimilares ? '*Productos similares encontrados:*' : '*No se encontraron productos similares*'}

${listaProductosSimilares}

━━━━━━━━━━━━━━━━━━━━

¿Qué deseas hacer?

${hayProductosSimilares ? '*1️⃣ Vincular con producto similar*\nResponde con el número del producto\n\n' : ''}*2️⃣ Es gasto sin inventario*
Escribe: *2*
(materia prima, insumo, etc.)

*3️⃣ Crear nuevo producto*
Escribe: *3*
(producto terminado para vender)

*4️⃣ Ver inventario completo*
Escribe: *4*

━━━━━━━━━━━━━━━━━━━━
Responde: ${hayProductosSimilares ? 'número del producto, ' : ''}2, 3 o 4`;

return [{
  json: {
    mensaje: mensaje,
    telefono: $('Webhook').first().json.body.data.key.remoteJid,
    instancia: $('Webhook').first().json.body.instance,
    // Guardar datos para el siguiente paso
    datos_producto: datos
  }
}];

// Obtener el mensaje del usuario
const mensajeUsuario = $('IF1').item.json.final_message.trim();

// Obtener los datos del estado pendiente
const estadoPendiente = $input.first().json;
const datosProducto = estadoPendiente.datos_contexto;

// Detectar la respuesta del usuario
let tipoRespuesta = null;
let numeroProducto = null;

// Verificar si es un número (producto similar o del inventario)
if (/^\d+$/.test(mensajeUsuario)) {
  const numero = parseInt(mensajeUsuario);

  // Verificar si es una opción especial (2, 3, 4)
  if (numero === 2) {
    tipoRespuesta = 'gasto_sin_inventario';
  } else if (numero === 3) {
    tipoRespuesta = 'crear_producto_nuevo';
  } else if (numero === 4) {
    tipoRespuesta = 'ver_inventario_completo';
  } else {
    // Es un número de producto
    tipoRespuesta = 'vincular_producto';
    numeroProducto = numero;
  }
} else {
  tipoRespuesta = 'respuesta_invalida';
}

// Retornar el resultado
return [{
  json: {
    tipo_respuesta: tipoRespuesta,
    numero_producto: numeroProducto,
    mensaje_usuario: mensajeUsuario,
    estado_id: estadoPendiente.id,
    datos_producto: datosProducto,
    sessionid: estadoPendiente.sessionid
  }
}];
