// Obtener el texto desde la ruta indicada
let texto = $input.first().json.estado.mensaje_original || "";

// Diccionario b√°sico de n√∫meros en palabras a n√∫meros
const numeros = {
  "cero": "0",
  "uno": "1", "una": "1", "un": "1",
  "dos": "2",
  "tres": "3",
  "cuatro": "4",
  "cinco": "5",
  "seis": "6",
  "siete": "7",
  "ocho": "8",
  "nueve": "9",
  "diez": "10",
  "once": "11",
  "doce": "12",
  "trece": "13",
  "catorce": "14",
  "quince": "15",
  "diecis√©is": "16", "dieciseis": "16",
  "diecisiete": "17",
  "dieciocho": "18",
  "diecinueve": "19",
  "veinte": "20",
  "veintiuno": "21", "veintiuna": "21",
  "veintid√≥s": "22", "veintidos": "22",
  "veintitr√©s": "23", "veintitres": "23",
  "veinticuatro": "24",
  "veinticinco": "25",
  "veintis√©is": "26", "veintiseis": "26",
  "veintisiete": "27",
  "veintiocho": "28",
  "veintinueve": "29",
  "treinta": "30",
  "cuarenta": "40",
  "cincuenta": "50",
  "sesenta": "60",
  "setenta": "70",
  "ochenta": "80",
  "noventa": "90",

};

// Funci√≥n para convertir palabras a n√∫meros o s√≠mbolos
function palabraANumero(palabra) {
  palabra = palabra.toLowerCase().trim();

  // Conversi√≥n de "punto" a "."
  if (palabra === "punto") {
    return ".";
  }

  if (numeros.hasOwnProperty(palabra)) {
    return numeros[palabra];
  }

  // Manejo de combinaciones como "treinta y cinco"
  if (palabra.includes(" y ")) {
    let partes = palabra.split(" y ").map(p => p.trim());
    if (numeros[partes[0]] && numeros[partes[1]]) {
      return (parseInt(numeros[partes[0]]) + parseInt(numeros[partes[1]])).toString();
    }
  }

  return palabra; // si no se reconoce, se deja igual
}

// Procesar el texto palabra por palabra
let palabras = texto.split(/(\s+)/); // Mantiene los espacios
let resultado = palabras.map(palabra => {
  // Solo procesar si no es un espacio y contiene letras
  if (palabra.trim() && /[a-z√°√©√≠√≥√∫√±]/i.test(palabra)) {
    return palabraANumero(palabra);
  }
  return palabra;
}).join('');

return { texto_convertido: resultado };

// Entrada esperada: puede venir como string o como array
// Ejemplo 1: "qrpa"
// Ejemplo 2: ["qrpa", "arepa"]

let entrada = $json.productos_detectados || $json.final_message || "";

// Si es string, lo convertimos en array de una sola palabra
if (typeof entrada === "string") {
  entrada = [entrada.trim()];
}

// Si no es array, lo forzamos a array
if (!Array.isArray(entrada)) {
  entrada = [String(entrada)];
}

// Normalizamos: quitamos vac√≠os
entrada = entrada.filter(p => p && p.trim() !== "");

// Ahora devolvemos un array de objetos, uno por producto
return entrada.map(p => ({
  json: { producto_detectado: p }
}));

function normalizar(s) {
  if (s === undefined || s === null) return "";
  return String(s)
    .trim()
    .toLowerCase()
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "")
    .replace(/\s+/g, " ");
}

function levenshtein(a, b) {
  const m = a.length, n = b.length;
  if (m === 0) return n;
  if (n === 0) return m;
  const dp = Array.from({ length: m + 1 }, () => new Array(n + 1).fill(0));
  for (let i = 0; i <= m; i++) dp[i][0] = i;
  for (let j = 0; j <= n; j++) dp[0][j] = j;
  for (let i = 1; i <= m; i++) {
    for (let j = 1; j <= n; j++) {
      const cost = a[i - 1] === b[j - 1] ? 0 : 1;
      dp[i][j] = Math.min(
        dp[i - 1][j] + 1,
        dp[i][j - 1] + 1,
        dp[i - 1][j - 1] + cost
      );
    }
  }
  return dp[m][n];
}

function similitud(a, b) {
  if (!a && !b) return 1;
  if (!a || !b) return 0;
  const dist = levenshtein(a, b);
  return 1 - dist / Math.max(a.length, b.length);
}

function extraerMarcados(texto) {
  if (!texto) return [];
  const matches = String(texto).match(/"([^"]+)"/g);
  if (!matches) return [];
  return matches.map(m => m.replace(/"/g, ""));
}

const UMBRAL_ACEPTAR = 0.80;
const UMBRAL_SUGERIR = 0.60;

const itemProducto = $input.first();
const mensajeOriginal = itemProducto.json.producto_detectado || "";
const apodos = $input.all().filter(item => item.json.APODO !== undefined);
const detectados = extraerMarcados(mensajeOriginal);

if (detectados.length === 0) {
  return [{
    json: {
      mensaje_original: mensajeOriginal,
      estado: {
        requiere_confirmacion: false,
        mensaje_procesado: mensajeOriginal,
        productos_resueltos: [],
        reemplazos_realizados: [],
        sugerencias: [],
        productos_no_encontrados: []
      },
      mensaje_final: mensajeOriginal,
      sin_productos: true
    }
  }];
}

let mensajeProcesado = mensajeOriginal;
let todosLosReemplazos = [];
let productosNoEncontrados = [];
let sugerenciasAcumuladas = [];
let productosResueltos = []; // NUEVO: Lista de productos ya resueltos

// üî• PROCESAR CADA PRODUCTO DETECTADO
for (const detectado of detectados) {
  const entrada = normalizar(detectado);

  if (!entrada) continue;

  // 1) Coincidencia exacta
  let exacto = apodos.find(f => normalizar(f.json.APODO) === entrada);
  if (exacto) {
    const regex = new RegExp(`"${detectado}"`, 'g');
    mensajeProcesado = mensajeProcesado.replace(regex, exacto.json.PRODUCTO);
    todosLosReemplazos.push({
      original: detectado,
      reemplazado: exacto.json.PRODUCTO,
      tipo: "exacto"
    });

    // NUEVO: Extraer la parte resuelta del mensaje
    const parteResuelta = mensajeOriginal.match(new RegExp(`\\d+\\s+"${detectado}"`, 'g'));
    if (parteResuelta) {
      const cantidadYProducto = parteResuelta[0].replace(`"${detectado}"`, exacto.json.PRODUCTO);
      productosResueltos.push(cantidadYProducto);
    }

    continue;
  }

  // 2) Calcular similitud
  let comparaciones = [];
  for (const fila of apodos) {
    const apodo = normalizar(fila.json.APODO);
    const score = similitud(entrada, apodo);
    comparaciones.push({
      producto: fila.json.PRODUCTO,
      apodo,
      score
    });
  }

  comparaciones.sort((a, b) => b.score - a.score);
  const top3 = comparaciones.slice(0, 3);

  // 3) Decisi√≥n por producto
  if (top3[0] && top3[0].score >= UMBRAL_ACEPTAR) {
    const regex = new RegExp(`"${detectado}"`, 'g');
    mensajeProcesado = mensajeProcesado.replace(regex, top3[0].producto);
    todosLosReemplazos.push({
      original: detectado,
      reemplazado: top3[0].producto,
      tipo: "similitud_alta",
      confianza: top3[0].score
    });

    // NUEVO: Extraer la parte resuelta del mensaje
    const parteResuelta = mensajeOriginal.match(new RegExp(`\\d+\\s+"${detectado}"`, 'g'));
    if (parteResuelta) {
      const cantidadYProducto = parteResuelta[0].replace(`"${detectado}"`, top3[0].producto);
      productosResueltos.push(cantidadYProducto);
    }

  } else if (top3[0] && top3[0].score >= UMBRAL_SUGERIR) {
    const productosUnicos = [];
    const productosVistos = new Set();

    for (const comparacion of top3) {
      if (!productosVistos.has(comparacion.producto)) {
        productosVistos.add(comparacion.producto);
        productosUnicos.push(comparacion);
      }
    }

    sugerenciasAcumuladas.push({
      apodo_usado: detectado,
      opciones: productosUnicos.map((r, i) => ({
        opcion: i + 1,
        producto: r.producto,
        apodo: r.apodo,
        confianza: Number(r.score.toFixed(3))
      }))
    });
  } else {
    productosNoEncontrados.push(detectado);
  }
}

// üéØ DECIDIR QUE RESPUESTA DAR

// 1) Si hay productos no encontrados
if (productosNoEncontrados.length > 0) {
  const listaNoEncontrados = productosNoEncontrados.map(p => `"${p}"`).join(", ");
  return [{
    json: {
      mensaje_original: mensajeOriginal,
      estado: {
        requiere_confirmacion: false,
        mensaje_procesado: mensajeOriginal,
        productos_resueltos: productosResueltos,
        reemplazos_realizados: todosLosReemplazos,
        sugerencias: [],
        productos_no_encontrados: productosNoEncontrados
      },
      mensaje_final: `‚ùå No encontr√© ${listaNoEncontrados} en la base de datos.\n¬øPodr√≠as verificar el nombre de estos productos?`,
      sin_coincidencia: true
    }
  }];
}

// 2) Si hay sugerencias pendientes
if (sugerenciasAcumuladas.length > 0) {
  const primeraSugerencia = sugerenciasAcumuladas[0];

  let opciones = primeraSugerencia.opciones.map(s => {
    let numEmoji = ["1Ô∏è‚É£","2Ô∏è‚É£","3Ô∏è‚É£","4Ô∏è‚É£","5Ô∏è‚É£"][s.opcion - 1] || `${s.opcion})`;
    return `${numEmoji} ${s.producto}`;
  }).join("\n");

// NUEVO: Mensaje que muestra productos ya resueltos PRIMERO
let mensajeConContexto = "";
if (productosResueltos.length > 0) {
  mensajeConContexto = `‚úÖ Ya confirm√©: ${productosResueltos.join(", ")}\n\n`;
}
mensajeConContexto += `ü§î No estuve 100% seguro con "${primeraSugerencia.apodo_usado}".\n\n¬øTe refieres a alguna de estas opciones?\n\n${opciones}\n\nüí¨ Responde con el n√∫mero`;

  return [{
    json: {
      mensaje_original: mensajeOriginal,
      estado: {
        requiere_confirmacion: true,
        mensaje_procesado: mensajeProcesado,
        productos_resueltos: productosResueltos, // NUEVO: Productos ya confirmados
        reemplazos_realizados: todosLosReemplazos,
        sugerencias: primeraSugerencia.opciones,
        productos_no_encontrados: [],
        apodo_usado: primeraSugerencia.apodo_usado // NUEVO: Para reemplazar despu√©s
      },
      mensaje_final: mensajeConContexto,
      requiere_confirmacion: true,
      apodo_usado: primeraSugerencia.apodo_usado,
      productos_restantes: sugerenciasAcumuladas.slice(1)
    }
  }];
}

// 3) Todos los productos fueron procesados exitosamente
return [{
  json: {
    mensaje_original: mensajeOriginal,
    estado: {
      requiere_confirmacion: false,
      mensaje_procesado: mensajeProcesado,
      productos_resueltos: productosResueltos,
      reemplazos_realizados: todosLosReemplazos,
      sugerencias: [],
      productos_no_encontrados: []
    },
    mensaje_final: mensajeProcesado,
    productos_reemplazados: todosLosReemplazos.length > 0,
    total_productos_procesados: detectados.length
  }
}];

const mensaje = String($json.texto_convertido || "").trim();
const sessionid = $json.sessionid;
const esNumero = /^[1-9]\d*$/.test(mensaje);

// Si hay estado en Postgres, lo obtenemos del Merge
const estadoItems = $items("Verificar estado pendiente");
const tieneEstadoPendiente = estadoItems.length > 0 && estadoItems[0].json.estado;

if (esNumero && tieneEstadoPendiente) {
  const estado = estadoItems[0].json.estado;

  if (estado.requiere_confirmacion && estado.sugerencias) {
    const indice = parseInt(mensaje, 10) - 1;
    const sugerencia = estado.sugerencias[indice];

    if (!sugerencia) {
      return [{
        json: {
          mensaje_final: "‚ùå Opci√≥n inv√°lida. Responde con un n√∫mero de la lista.",
          enviar_directo: true,
          mantener_estado: true,
          sessionid: sessionid,
          accion: "error_opcion"
        }
      }];
    }

    // NUEVO: Reconstruir mensaje completo
    let mensajeFinal = "";

    // 1. Agregar productos ya resueltos
    if (estado.productos_resueltos && estado.productos_resueltos.length > 0) {
      mensajeFinal = estado.productos_resueltos.join(" y ");
    }

    // 2. Agregar producto confirmado
    const mensajeOriginal = estado.mensaje_procesado || "";
    const regex = new RegExp(`"${estado.apodo_usado}"`, 'g');
    const parteConfirmada = mensajeOriginal.match(new RegExp(`\\d+\\s+"${estado.apodo_usado}"`, 'g'));

    if (parteConfirmada) {
      const cantidadYProducto = parteConfirmada[0].replace(`"${estado.apodo_usado}"`, sugerencia.producto);
      if (mensajeFinal) {
        mensajeFinal += " y " + cantidadYProducto;
      } else {
        mensajeFinal = cantidadYProducto;
      }
    }

    return [{
      json: {
        final_message: mensajeFinal,
        sessionid: sessionid,
        datetime: $json.datetime,
        es_respuesta_sugerencia: true,
        producto_confirmado: true,
        eliminar_estado: true,
        accion: "confirmar_sugerencia",
        // NUEVOS CAMPOS PARA SHEETS:
        apodo_confirmado: estado.apodo_usado,        // "qrepa"
        producto_seleccionado: sugerencia.producto  // "Arepas de QUESO"
      }
    }];
  }
}

// Si el usuario env√≠a algo diferente mientras tiene sugerencias pendientes
if (tieneEstadoPendiente) {
  return [{
    json: {
      final_message: $json.texto_convertido,
      sessionid: sessionid,
      datetime: $json.datetime,
      es_respuesta_sugerencia: false,
      eliminar_estado: true,
      procesar_nuevo_mensaje: true,
      accion: "limpiar_y_procesar"
    }
  }];
}

// Flujo normal - no hay estado pendiente
return [{
  json: {
    final_message: $json.texto_convertido,
    sessionid: sessionid,
    datetime: $json.datetime,
    es_respuesta_sugerencia: false,
    accion: "flujo_normal"
  }
}];

// En lugar de referenciar otros nodos, usar el input directo
return [{
  json: {
    texto_para_administrador: $json.mensaje_final || $json.final_message || "Error"
  }
}];
