// Capturamos la respuesta de Gemini desde la estructura correcta
let respuestaGemini = String($input.first().json.content?.parts?.[0]?.text || '').trim();

// Limpiamos los bloques de markdown
respuestaGemini = respuestaGemini.replace(/```json\n?/g, '').replace(/\n?```/g, '').trim();

// Parseamos el JSON
let productosExtraidos;
try {
  productosExtraidos = JSON.parse(respuestaGemini);
} catch (error) {
  return [{ 
    json: { 
      error: "No se pudo procesar la respuesta de Gemini", 
      respuesta_original: respuestaGemini 
    } 
  }];
}

// Procesamos el array de productos
const resultados = productosExtraidos.map((item, index) => {

  // Preparamos los datos (aceptando may√∫sculas o min√∫sculas)
  const fila = {
    producto: item.PRODUCTO || item.producto || "",
    unidad_medida: item.UNIDAD_MEDIDA || item.unidad_medida || "",
    cantidad: parseFloat(item.CANTIDAD || item.cantidad || 0),
    fecha: new Date().toLocaleString('es-CO', { timeZone: 'America/Bogota' }),
    errores: []
  };

  // Validaciones con mensajes personalizados
  if (!fila.producto || fila.producto === "") {
    fila.errores.push("No se pudo identificar el producto. Vuelve a ingresar esta compra.");
  }

  if (!fila.cantidad || fila.cantidad <= 0 || isNaN(fila.cantidad)) {
    fila.errores.push(`No especificaste la cantidad para ${fila.producto || 'este producto'}. Vuelve a ingresar esta compra.`);
  }

  if (!fila.unidad_medida || fila.unidad_medida === "") {
    fila.errores.push(`No especificaste la unidad de medida para ${fila.producto || 'este producto'}. Vuelve a ingresar esta compra.`);
  }

  // Log del resultado
  if (fila.errores.length === 0) {
    console.log(`‚úÖ ${index + 1}. ${fila.producto} - ${fila.cantidad} ${fila.unidad_medida}`);
  } else {
    console.log(`‚ùå ${index + 1}. ${fila.producto}: ${fila.errores.join(' | ')}`);
  }

  return { json: fila };
});

console.log(`üìä Total procesado: ${resultados.length} producto(s)`);

return resultados;

try {
  // ======= OBTENER DATOS DE M√öLTIPLES COMPRAS =======
  const comprasData = $("Procesar_compra").all();
  console.log(`üì¶ Procesando ${comprasData.length} compras`);

  // Validaci√≥n inicial
  if (!comprasData || comprasData.length === 0) {
    throw new Error("No hay datos de compras para procesar");
  }

  // ======= OBTENER INVENTARIO ACTUAL =======
  const inventarioItems = $input.all(); // Todos los items del inventario
  console.log(`üìä Items de inventario disponibles: ${inventarioItems.length}`);

  // Crear un mapa del inventario por producto para b√∫squeda r√°pida
  const inventarioMap = {};
  inventarioItems.forEach(item => {
    if (item.json && item.json.producto) {
      const producto = item.json.producto.trim();
      inventarioMap[producto] = item.json;
    }
  });

  console.log(`üìã Productos en inventario:`, Object.keys(inventarioMap));

  // ======= PROCESAR CADA COMPRA INDIVIDUALMENTE =======
  const resultados = [];

  comprasData.forEach((compraItem, index) => {
    try {
      const compra = compraItem.json;
      console.log(`\nüîç Procesando compra ${index + 1}: ${compra.producto}`);

      // Validaci√≥n de datos de compra
      if (!compra || !compra.producto) {
        throw new Error(`Compra ${index + 1}: Datos inv√°lidos - falta informaci√≥n del producto`);
      }

      // Buscar este producto espec√≠fico en el inventario
      const producto = compra.producto.trim();
      const inventario = inventarioMap[producto];

      console.log(`   üìã Producto: ${producto}`);

      // VALIDACI√ìN ESTRICTA: El producto DEBE existir en el inventario
      if (!inventario || !inventario.producto) {
        throw new Error(`${producto}: No existe en el inventario. Use el nombre exacto del inventario.`);
      }

      console.log(`   üìç Producto encontrado en inventario: ‚úÖ`);

      // Procesar datos del producto
      const cantidadCompra = Number(compra.cantidad) || 0;
      const unidadMedida = compra.unidad_medida || "cajas";

      // Validaciones adicionales
      if (cantidadCompra <= 0) {
        throw new Error(`${producto}: La cantidad debe ser mayor a 0`);
      }

      // Stock actual
      const stockActual = Number(inventario.stock_actual_unidades) || 0;

      // ======= L√ìGICA DE C√ÅLCULO SEG√öN UNIDAD DE MEDIDA =======
      let unidadesIngresadas;

      if (unidadMedida === "unidades") {
        // Suma directa sin usar factor
        unidadesIngresadas = cantidadCompra;
      } else {
        // Usa el factor para cajas, pacas, paquetes, litros, botellas, etc.
        const factor = inventario.factor_unidades || 0;
        unidadesIngresadas = cantidadCompra * factor;
      }

      // Calcular nuevo stock
      const nuevoStock = stockActual + unidadesIngresadas;

      // Fecha actual en zona horaria de Colombia
      const fechaActual = new Date().toLocaleString('es-CO', { 
        timeZone: 'America/Bogota',
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit'
      });

      // Log del procesamiento
      console.log(`   üìä Stock: ${stockActual} ‚Üí ${nuevoStock} (+${unidadesIngresadas})`);

      // Crear resultado para este producto
      const resultado = {
        json: {
          producto: producto,
          unidad_medida: unidadMedida,
          factor_unidades: inventario.factor_unidades,
          cantidad: cantidadCompra,
          stock_actual_unidades: nuevoStock,
          fecha_ultima_compra: fechaActual,
          unidades_ingresadas: unidadesIngresadas,
          stock_anterior: stockActual,
          accion: "actualizar",
          // Campos adicionales para tracking
          row_number: inventario.row_number || null,
          procesado_en_lote: true,
          indice_compra: index + 1
        }
      };

      resultados.push(resultado);
      console.log(`   ‚úÖ Producto procesado exitosamente`);

    } catch (error) {
      console.error(`   ‚ùå Error procesando compra ${index + 1}:`, error.message);

      // Agregar resultado de error para este producto espec√≠fico
      resultados.push({
        json: {
          error: true,
          mensaje: error.message,
          producto_con_error: compraItem.json?.producto || 'Desconocido',
          indice_compra: index + 1,
          timestamp: new Date().toISOString()
        }
      });
    }
  });

  // ======= RESUMEN FINAL =======
  const exitosos = resultados.filter(r => !r.json.error).length;
  const conError = resultados.filter(r => r.json.error).length;

  console.log(`\nüìä RESUMEN FINAL:`);
  console.log(`   Total compras: ${comprasData.length}`);
  console.log(`   Procesadas exitosamente: ${exitosos}`);
  console.log(`   Con errores: ${conError}`);

  // Retornar TODOS los resultados
  return resultados;

} catch (error) {
  console.error("‚ùå Error general procesando compras:", error.message);

  // Retornar error estructurado general
  return [{
    json: {
      error: true,
      mensaje: `Error general: ${error.message}`,
      tipo_error: "error_general",
      timestamp: new Date().toISOString()
    }
  }];
}

// Obtener todos los datos del merge
const inputData = $input.all();

// Obtener fecha y hora actual en zona horaria de Bogot√°
const fechaHora = new Date().toLocaleString('es-CO', {
  timeZone: 'America/Bogota',
  day: '2-digit',
  month: '2-digit', 
  year: 'numeric',
  hour: '2-digit',
  minute: '2-digit',
  second: '2-digit',
  hour12: true
});

// Generar l√≠neas de confirmaci√≥n para cada entrada
const lineasConfirmacion = inputData.map(item => {
  const entrada = item.json;
  const producto = entrada.producto;
  const cantidad = entrada.cantidad;
  const unidadMedida = entrada.unidad_medida;
  const codigoEntrada = entrada.codigo_entrada; // A006, A007, etc.
  const nuevoStock = entrada.stock_actual_unidades; // Ya viene en el mismo objeto

  // Mostrar con c√≥digo de entrada y stock actualizado
  return `[${codigoEntrada}] ${cantidad} ${unidadMedida} de ${producto} / nuevoStock: ${nuevoStock}`;
});

// Construir el mensaje completo
const mensaje = [
  "‚úÖ Registro de entrada a inventario",
  fechaHora,
  "",  // L√≠nea vac√≠a
  ...lineasConfirmacion
].join('\n');

// Retornar el mensaje
return [{ 
  json: {
    mensaje: mensaje,
    mensaje_texto: mensaje,
    timestamp: fechaHora,
    entradasRegistradas: lineasConfirmacion.length
  }
}];
