// Capturamos el texto desde la propiedad correcta
const texto = String($input.first().json.output || '').trim();

// Regex para extraer "producto" cantidad
const regex = /"([^"]+)"\s+(\d+(?:\.\d+)?)/g;

const filas = [];
let match;

while ((match = regex.exec(texto)) !== null) {
  filas.push({
    PRODUCTO: match[1].replace(/_/g, ' '),
    CANTIDAD: parseFloat(match[2]),
    FECHA: new Date().toLocaleString('es-CO', { timeZone: 'America/Bogota' })
  });
}

return filas;

// Acceder a TODOS los items del nodo de consumos
let consumosPersonales = [];
// Usar $items para obtener todos los items del nodo espec√≠fico
try {
  const todosLosConsumos = $items("ingresa_consumos_personales");
  if (todosLosConsumos && Array.isArray(todosLosConsumos)) {
    consumosPersonales = todosLosConsumos.map(item => item.json);
  }
} catch (error) {
  // M√©todo fallback si $items no funciona
  const consumosDirectos = $node["ingresa_consumos_personales"];
  if (consumosDirectos && consumosDirectos.json) {
    consumosPersonales = [consumosDirectos.json];
  }
}

// Procesar inventario (extraer datos json)
const inventarioData = $input.all();
const inventarioItems = [];
inventarioData.forEach(item => {
  if (item.json && item.json.stock_actual_unidades !== undefined) {
    inventarioItems.push(item.json);
  }
});

// Verificar que tenemos datos v√°lidos - mejorar el manejo de errores
if (consumosPersonales.length === 0) {
  // Si no hay consumos, no hacer nada y retornar vac√≠o (no error)
  return [];
}

if (inventarioItems.length === 0) {
  // Si no hay inventario a√∫n, no hacer nada y retornar vac√≠o (no error)
  return [];
}

// Eliminar duplicados del inventario bas√°ndose en producto y row_number
const inventarioSinDuplicados = inventarioItems.filter((item, index, self) => 
  index === self.findIndex(i => i.producto === item.producto && i.row_number === item.row_number)
);

// Crear un mapa de consumos por producto
const consumosPorProducto = {};
consumosPersonales.forEach(consumo => {
  const producto = consumo.producto;
  const cantidad = consumo.cantidad;

  if (consumosPorProducto[producto]) {
    consumosPorProducto[producto] += cantidad;
  } else {
    consumosPorProducto[producto] = cantidad;
  }
});

// Procesar el inventario y descontar los consumos
const inventarioActualizado = inventarioSinDuplicados.map(item => {
  const producto = item.producto;
  const stockActual = item.stock_actual_unidades;

  // Verificar si este producto fue consumido
  if (consumosPorProducto[producto]) {
    const cantidadConsumida = consumosPorProducto[producto];
    const nuevoStock = Math.max(0, stockActual - cantidadConsumida);

    return {
      ...item,
      stock_actual_unidades: nuevoStock
    };
  }

  // Si no fue consumido, mantener el stock actual
  return item;
});

// Retornar solo el inventario actualizado
return inventarioActualizado;

// Obtener los datos de los consumos y el inventario actualizado
const consumosData = $items("ingresa_consumos_personales");
const inventarioActualizado = $input.all();

// Obtener fecha y hora actual en zona horaria de Bogot√°
const fechaHora = new Date().toLocaleString('es-CO', {
  timeZone: 'America/Bogota',
  day: '2-digit',
  month: '2-digit', 
  year: 'numeric',
  hour: '2-digit',
  minute: '2-digit',
  second: '2-digit',
  hour12: true
});

// Crear mapa del inventario actualizado por producto
const stockActualizado = {};
inventarioActualizado.forEach(item => {
  if (item.json) {
    stockActualizado[item.json.producto] = item.json.stock_actual_unidades;
  }
});

// Lista de productos que NO deben mostrar nuevoStock
const productosSinNuevoStock = ["Arepa con queso", "Arepa BURGUER", "Chorizos"];

// Generar l√≠neas de confirmaci√≥n para cada consumo
const lineasConfirmacion = consumosData.map(consumo => {
  const producto = consumo.json.producto;
  const cantidad = consumo.json.cantidad;
  const nuevoStock = stockActualizado[producto] || 'N/A';

  if (productosSinNuevoStock.includes(producto)) {
    return `${cantidad} ${producto}`;
  } else {
    return `${cantidad} ${producto} / nuevoStock : ${nuevoStock}`;
  }
});

// Construir el mensaje completo
const mensaje = [
  "‚úÖ Registro de consumo personal",
  fechaHora,
  "",  // L√≠nea vac√≠a para agregar espacio
  ...lineasConfirmacion
].join('\n');

// Retornar el mensaje
return [{ 
  mensaje: mensaje,
  timestamp: fechaHora,
  consumosRegistrados: lineasConfirmacion.length
}];
